import "../Main"; // this will link the main CSS file for style
import { signInWithEmailAndPassword } from "firebase/auth";
import React, { useState } from "react";
import { auth } from "../../firebase";

const SignIn = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const signIn = (e) => {
    e.preventDefault();

    if(!email.endsWith("@moreheadstate.edu")){
      console.log("Must be an MSU EMAIL!!!!!");
      alert("Must be an MSU Email to Sign In.");
    }else{
      signInWithEmailAndPassword(auth, email, password)
      .then((userCredential) => {
        console.log(userCredential);
        // this will redirect to dashboard page
        window.location.href = "dashboard.html";
      })
      .catch((error) => {
        console.log(error);
      });
    }
  };

  return (
    <div className="sign-in-container">
      <form id="sign-in-form" onSubmit={signIn}>
        <h1>Log In to your Account</h1>
        <label htmlFor="email">Email:</label>
        <input
          type="email"
          id="email"
          placeholder="Enter your email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        ></input>
        <input
          type="password"
          id="password"
          placeholder="Enter your password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        ></input>
        <button type="submit" id="signInButton">Log In</button>
      </form>
    </div>
  );
};

export default SignIn;
